import React, { useState } from 'react';
import { ChevronRight, ChevronLeft, Users, Car, MapPin, Shield, Star, MessageCircle } from 'lucide-react';

const RoadRaveSurvey = () => {
  // ...existing code...

  // REPLACE ALL LOGIC AND SECTIONS WITH THE USER'S LATEST VERSION (see user message)

  const [currentSection, setCurrentSection] = useState(0);
  const [responses, setResponses] = useState({});

  const updateResponse = (key, value) => {
    setResponses(prev => ({
      ...prev,
      [key]: value
    }));
  };

  const nextSection = () => {
    if (currentSection < sections.length - 1) {
      setCurrentSection(currentSection + 1);
    }
  };

  const prevSection = () => {
    if (currentSection > 0) {
      setCurrentSection(currentSection - 1);
    }
  };

  const handleMultiSelect = (key, value) => {
    const current = responses[key] || [];
    const updated = current.includes(value) 
      ? current.filter(item => item !== value)
      : [...current, value];
    updateResponse(key, updated);
  };

  const LikertScale = ({ question, responseKey }) => (
    <div className="space-y-4">
      <h3 className="text-lg font-semibold text-white mb-4">{question}</h3>
      <div className="flex justify-between items-center bg-white/10 backdrop-blur-sm rounded-lg p-4">
        {['Very Unlikely', 'Unlikely', 'Neutral', 'Likely', 'Very Likely'].map((option, index) => (
          <label key={index} className="flex flex-col items-center cursor-pointer">
            <input
              type="radio"
              name={responseKey}
              value={index + 1}
              checked={responses[responseKey] === index + 1}
              onChange={() => updateResponse(responseKey, index + 1)}
              className="mb-2"
            />
            <span className="text-sm text-white text-center">{option}</span>
          </label>
        ))}
      </div>
    </div>
  );

  const sections = [
    // ...all sections from user message...
    // Landing Page
    {
      title: "Welcome to RoadRave",
      background: "bg-gradient-to-br from-purple-600 via-pink-600 to-red-600 bg-cover bg-center relative",
      backgroundImage: "url('https://images.unsplash.com/photo-1540575467063-178a50c2df87?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')",
      content: (
        <div className="text-center space-y-8">
          <div className="bg-white/10 backdrop-blur-sm rounded-2xl p-8 mx-auto max-w-2xl">
            <h1 className="text-4xl font-bold text-white mb-6">🎉 RoadRave</h1>
            <p className="text-xl text-white/90 mb-6">
              Connect with fellow event-goers, share rides, and make every journey as exciting as the destination!
            </p>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6 text-center">
              <div className="bg-white/10 rounded-lg p-4">
                <Users className="w-8 h-8 text-white mx-auto mb-2" />
                <h3 className="font-semibold text-white">Find Travel Buddies</h3>
                <p className="text-sm text-white/80">Connect with verified event attendees</p>
              </div>
              <div className="bg-white/10 rounded-lg p-4">
                <Car className="w-8 h-8 text-white mx-auto mb-2" />
                <h3 className="font-semibold text-white">Share Rides</h3>
                <p className="text-sm text-white/80">Split costs and reduce environmental impact</p>
              </div>
              <div className="bg-white/10 rounded-lg p-4">
                <Shield className="w-8 h-8 text-white mx-auto mb-2" />
                <h3 className="font-semibold text-white">Safe & Verified</h3>
                <p className="text-sm text-white/80">Ticket verification and safety features</p>
              </div>
            </div>
            <p className="text-lg text-white/90 mt-8">
              Help us build the perfect event travel companion by sharing your thoughts and experiences!
            </p>
          </div>
        </div>
      )
    },
    // Section 1: Personal Information
    {
      title: "Personal Information",
      background: "bg-gradient-to-br from-blue-600 via-indigo-600 to-purple-600 bg-cover bg-center relative",
      backgroundImage: "url('https://images.unsplash.com/photo-1522202176988-66273c2fd55f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2071&q=80')",
      content: (
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">What's your age?</label>
            <input
              type="number"
              placeholder="Enter your age"
              value={responses.age || ''}
              onChange={(e) => updateResponse('age', e.target.value)}
              className="w-full p-3 rounded-lg bg-white/20 text-white placeholder-white/70 border border-white/30"
            />
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">Which best describes you?</label>
            <select
              value={responses.occupation || ''}
              onChange={(e) => updateResponse('occupation', e.target.value)}
              className="w-full p-3 rounded-lg bg-white/20 text-white border border-white/30"
            >
              <option value="">Select an option</option>
              <option value="student">Student</option>
              <option value="professional">Working Professional</option>
              <option value="freelancer">Freelancer</option>
              <option value="retired">Retired</option>
              <option value="other">Other</option>
            </select>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">Which region of the UK do you live in?</label>
            <select
              value={responses.region || ''}
              onChange={(e) => updateResponse('region', e.target.value)}
              className="w-full p-3 rounded-lg bg-white/20 text-white border border-white/30"
            >
              <option value="">Select a region</option>
              <option value="london">London</option>
              <option value="southeast">South East</option>
              <option value="southwest">South West</option>
              <option value="midlands">Midlands</option>
              <option value="north">North</option>
              <option value="scotland">Scotland</option>
              <option value="wales">Wales</option>
              <option value="ni">Northern Ireland</option>
            </select>
          </div>
        </div>
      )
    },
    // Section 2: Event Attendance & Pain Points
    {
      title: "Event Attendance & Pain Points",
      background: "bg-gradient-to-br from-green-600 via-teal-600 to-blue-600 bg-cover bg-center relative",
      backgroundImage: "url('https://images.unsplash.com/photo-1493225457124-a3eb161ffa5f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')",
      content: (
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">How often do you attend live events (concerts, festivals, sports) in a year?</label>
            <div className="grid grid-cols-2 gap-3">
              {['Never', '1-2', '3-5', '6+'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="eventFrequency"
                    value={option}
                    checked={responses.eventFrequency === option}
                    onChange={(e) => updateResponse('eventFrequency', e.target.value)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">When you attend an event, do you usually go alone or with others?</label>
            <div className="space-y-2">
              {['Always alone', 'Mostly alone', 'Mostly with friends/family', 'Always with friends/family'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="attendancePattern"
                    value={option}
                    checked={responses.attendancePattern === option}
                    onChange={(e) => updateResponse('attendancePattern', e.target.value)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">Have you ever missed an event because you didn't have anyone to go with?</label>
            <div className="flex space-x-4">
              {['Yes', 'No'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="missedEvents"
                    value={option}
                    checked={responses.missedEvents === option}
                    onChange={(e) => updateResponse('missedEvents', e.target.value)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">What is your biggest challenge when planning to attend an event? (Select all that apply)</label>
            <div className="space-y-2">
              {['Finding travel companions', 'High travel/accommodation cost', 'Scheduling or timing', 'Parking/transport info', 'Other'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(responses.challenges || []).includes(option)}
                    onChange={() => handleMultiSelect('challenges', option)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
        </div>
      )
    },
    // Section 3: Current Travel/Logistics Practices
    {
      title: "Current Travel & Logistics",
      background: "bg-gradient-to-br from-orange-600 via-red-600 to-pink-600 bg-cover bg-center relative",
      backgroundImage: "url('https://images.unsplash.com/photo-1449824913935-59a10b8d2000?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=2070&q=80')",
      content: (
        <div className="space-y-6">
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">When traveling to events, how do you usually get there?</label>
            <div className="space-y-2">
              {['Drive alone', 'Ride with friends', 'Public transport', 'Rideshare/taxi', 'Other'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="travelMethod"
                    value={option}
                    checked={responses.travelMethod === option}
                    onChange={(e) => updateResponse('travelMethod', e.target.value)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">Would you consider sharing the ride (and splitting costs) with someone you don't know who is going to the same event?</label>
            <div className="flex space-x-4">
              {['Yes', 'No'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="radio"
                    name="carpoolWillingness"
                    value={option}
                    checked={responses.carpoolWillingness === option}
                    onChange={(e) => updateResponse('carpoolWillingness', e.target.value)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
          <div className="bg-white/10 backdrop-blur-sm rounded-lg p-6">
            <label className="block text-white font-semibold mb-3">What concerns do you have about carpooling with other event-goers? (Select all that apply)</label>
            <div className="space-y-2">
              {['Safety/trust', 'Scheduling/sync', 'Vehicle comfort', 'No concerns', 'Other'].map(option => (
                <label key={option} className="flex items-center space-x-2 cursor-pointer">
                  <input
                    type="checkbox"
                    checked={(responses.carpoolConcerns || []).includes(option)}
                    onChange={() => handleMultiSelect('carpoolConcerns', option)}
                    className="text-white"
                  />
                  <span className="text-white">{option}</span>
                </label>
              ))}
            </div>
          </div>
